import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

  displayReports: boolean = false;

  showReportOptions(){
    this.displayReports = !this.displayReports
  }
}
